INSERT INTO public.movies (director_id, video_url, title_url, title, short_description, card_url, album_url, country_id, rating, long_description, release_date, movie_type)
VALUES
  (1, '/static/movies/squid-game/movie.mp4', '/static/movies/squid-game/logo.png', 'Игра в кальмара', 'Под маской детских игр и забавных заданий скрывается жестокая конкуренция, которая приводит к драматическим последствиям.', '/static/movies/squid-game/card.png', '/static/movies/squid-game/poster.png', 4, 7.6, 'Сон Ги-хун уже немолод, разведён, по уши погряз в долгах и сидит на шее у старенькой матери. Даже выигранные на скачках деньги в его руках долго не задерживаются, и однажды он встречает в метро загадочного незнакомца, который сначала предлагает сыграть в детскую игру, а затем вручает Ги-хуну немалую сумму и визитку. Но радость мужчины сменятся отчаянием, когда он узнаёт, что бывшая жена с новым мужем собираются увезти его дочь в Америку. Он звонит по номеру с визитки и становится последним участником тайных игр на выживание с призом в 40 миллионов долларов. Среди товарищей по несчастью оказываются его друг детства — прогоревший финансист, бандит, смертельно больной старик, северокорейская перебежчица, иммигрант из Пакистана и многие другие отчаянно нуждающиеся в деньгах.', '2021-09-17', 'serial'),
  (1, '/static/movies/paper_house/movie.mp4', '/static/movies/paper_house/logo.png', 'Бумажный дом', 'короткое описание', '', '/static/movies/paper_house/poster.png', 5, 8.2, 'История о преступниках, решивших ограбить Королевский монетный двор Испании и украсть 2,4 млрд евро.', '2017-05-02', 'serial'),
  (1, '/static/movies/kitcnen/movie.mp4', '/static/movies/kitcnen/logo.png', 'Кухня', 'Звёзды отечественного кинематографа в популярном ситкоме о закулисной жизни одного из столичных лакшери-ресторанов', '', '/static/movies/kitcnen/poster.png', 1, 8.2, 'Сага со звездным актерским составом об истории сотрудников одного московского ресторана высокой кухни. Заведение с громким названием «Клод Моне» славится на всю столицу своей изысканной кухней, от посетителей самого высокого ранга нет отбоя, а повара здесь самые настоящие небожители. Именно поэтому амбициозный кулинар из провинции Макс Лавров не видит себя нигде, кроме как в этой стройной и весьма колоритной команде — так что, чтобы закрепиться в «Клоде Моне», ему придется многое пережить и претерпеть.', '2012-10-22', 'serial'),
